import "./users.scss";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export const Users = () => {
  const [users, setusers] = useState([]);
  const [RunEffect, setRunEffect] = useState(0);
///////////////////////////////////////////////////
  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/user/show")
      .then((res) => res.json())

      .then((res) => {
        setusers(res);
      });
  }, [RunEffect]);

  const RemveUser = async (id) => {
    try {
      let res = await axios.delete(
        `http://127.0.0.1:8000/api/user/delete/${id}`
      );
      if (res.status === 200) {
        setRunEffect((prev) => prev + 1);
      }
    } catch {}
  };



  const Users = users.map((user, i) => (
    <tr key={i}>
      <td>{i + 1}</td>
      <td>{user.email}</td>
      <td>{user.name}</td>
      <td className="action">
        <Link to={`${user.id}`}>
          <i className="fa-solid fa-pen-to-square"></i>
        </Link>
        <i
          className="fa-sharp fa-solid fa-trash"
          onClick={() => RemveUser(user.id)}
        ></i>
      </td>
    </tr>
  ));

  return (
    <>
      <table className="usersTaple">
        <thead>
          <tr>
            <th>id</th>
            <th>email</th>
            <th>name</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>{Users}</tbody>
      </table>
    </>
  );
};
