import React, { useEffect, useState } from "react";
import "./updateUser.scss";
import axios from "axios";

export const UpdateUser = () => {
  const [FullName, setFullName] = useState("");
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [PassordConf, setPassordConf] = useState("");
  const [User, setUser] = useState([]);

  let errorMasseges = document.getElementsByClassName("errorMassege");
  let currentId = parseInt(window.location.pathname.split("/", -1)[3]);

  useEffect(() => {
    fetch(`http://127.0.0.1:8000/api/user/showbyid/${currentId}`)
      .then((res) => res.json())

      .then((data) => {
        setFullName(data[0].name);
        setEmail(data[0].email);
      });
  }, []);

  // let x = "2023-04-08T01:11:01.000000Z";
  // undefined;
  // x;
  // ("2023-04-08T01:11:01.000000Z");
  // x.sli;
  // undefined;
  // x.slice(0, 10);
  // ("2023-04-08");

  const submit = async (e) => {
    e.preventDefault();
    let nameLenght = (FullName.length >= 8) & (FullName.length <= 20),
      nameFormat = !/\d/.test(FullName),
      emailFormat = /^\w+([\\.-]?\w+)*@\w+([\\.-]?\w+)*(\.\w{2,3})+$/.test(
        Email
      ),
      passwordFormat =
        /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/.test(Password),
      passwordLenght = Boolean(
        (Password.length >= 8) & (Password.length <= 20)
      ),
      passwordConfFormat = Password === PassordConf;
    let submitCond =
      nameLenght &
      nameFormat &
      emailFormat &
      passwordFormat &
      passwordLenght &
      passwordConfFormat;
    try {
      if (submitCond) {
        // eslint-disable-next-line no-unused-vars
        let res = await axios
          .post(`http://127.0.0.1:8000/api/user/update/${currentId}`, {
            //stoped here
            name: FullName,
            email: Email,
            password: Password,
            password_confirmation: PassordConf,
          })
          .catch((reason) => {
            if (reason.response.status === 200) {
              console.log("edite done ");
            } else {
              console.log("errpr");
              window.location.pathname = "/dashboard/users";
            }
          });
      }
    } catch (reason) {}

      };

  const handelFullName = (userName) => {
    setFullName(userName);
    let nameLength =
        (userName.length >= 8) & (userName.length <= 20) ||
        userName.length === 0,
      nameFormat = !/\d/.test(userName);
    nameLength
      ? (errorMasseges[0].innerHTML = ``)
      : (errorMasseges[0].innerHTML = `*name must be 8:20`);
    nameFormat
      ? (errorMasseges[1].innerHTML = ``)
      : (errorMasseges[1].innerHTML = `* name must not contain nembers`);
    // if (nameLength & nameFormat) {
    //   fullNameContainer.classList.add("valid");
    //   fullNameContainer.classList.remove("notvalid");
    // } else {
    //   fullNameContainer.classList.add("notvalid");
    //   fullNameContainer.classList.remove("valid");
    // }
  };

  const handelEmail = (userEmail) => {
    setEmail(userEmail);
    let emailFormat = /^\w+([\\.-]?\w+)*@\w+([\\.-]?\w+)*(\.\w{2,3})+$/.test(
      userEmail
    );
    if (emailFormat || userEmail.length === 0) {
      errorMasseges[2].innerHTML = ``;
      // emailContainer.classList.add("valid");
      // emailContainer.classList.remove("notvalid");
    } else {
      errorMasseges[2].innerHTML = `*exam_3mple@gmail.com`;
      // emailContainer.classList.add("notvalid");
      // emailContainer.classList.remove("valid");
    }
  };

  const handelPassword = (userPassword) => {
    console.log(userPassword);
    setPassword(userPassword);
    // document.getElementById("confirmPasswordInput").value = "";
    let passwordLenght =
        (userPassword.length >= 8) & (userPassword.length <= 20),
      passwordFormat =
        /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$/.test(
          userPassword
        );
    passwordLenght
      ? (errorMasseges[3].innerHTML = ``)
      : (errorMasseges[3].innerHTML = `*must mor than 7`);
    passwordFormat
      ? (errorMasseges[4].innerHTML = ``)
      : (errorMasseges[4].innerHTML = `*format error`);
    // if (passwordLenght & passwordFormat) {
    //   passwordContainer.classList.add("valid");
    //   passwordContainer.classList.remove("notvalid");
    // } else {
    //   passwordContainer.classList.add("notvalid");
    //   passwordContainer.classList.remove("valid");
    // }
  };

  const handelPasswordConfirm = (passwordConf) => {
    setPassordConf(passwordConf);
    let passwordConfFormat = Password === passwordConf;
    if (passwordConfFormat) {
      // passwordConfContainer.classList.add("valid");
      // passwordConfContainer.classList.remove("notvalid");
      errorMasseges[5].innerHTML = ``;
    } else {
      // passwordConfContainer.classList.add("notvalid");
      // passwordConfContainer.classList.remove("valid");
      errorMasseges[5].innerHTML = `*passord did not match`;
    }
  };

  return (
    <div id="updateUserPerant">
      <section id="updateUserSec">
        <div className="overLay">
          <div className="contentBox">
            <h1 className="contentHeading">Update</h1>
            <p className="content">
              To keep contected with us please login with your personal info{" "}
            </p>
            <a href="#  " className="Btn">
              login
            </a>
          </div>
        </div>
        <div className="formContainer">
          <form
            method=""
            action=""
            id="updateUserForm"
            onSubmit={submit}
          >
            <h1 className="formHeading">Update</h1>
            <div className="socialBox">
              <i className="fa-brands fa-facebook" />
              <i className="fa-brands fa-google" />
              <i className="fa-brands fa-github" />
            </div>
            <p className="notice">or use your email for ragistertion</p>
            <div id="nameFieldBox" className=" inputContainer">
              <input
                type="text"
                id="nameInput"
                defaultValue={FullName}
                placeholder="fullName..."
                name="nameInput"
                onChange={(e) => {
                  // handelSubmition(e.target.value, e.target.name);
                  handelFullName(e.target.value);
                }}
              />
              <p className="errorMassege" />
              <p className="errorMassege" />
            </div>
            <div id="emailFieldBox" className=" inputContainer">
              <input
                type="email"
                id="emailInput"
                defaultValue={Email}
                placeholder="email..."
                name="emailInput"
                onChange={(e) => {
                  handelEmail(e.target.value);
                }}
              />
              <p className="errorMassege" />
            </div>
            <div id="passwordFieldBox" className=" inputContainer">
              <input
                type="password"
                id="passwordInput"
                // vlue={Password}
                placeholder="password..."
                name="passwordInput"
                onChange={(e) => {
                  handelPassword(e.target.value);
                }}
              />
              <p className="errorMassege" />
              <p className="errorMassege" />
            </div>
            <div id="passwordConfFieldBox" className=" inputContainer">
              <input
                type="password"
                id="passwordConfInput"
                // vlue={PassordConf}
                placeholder="confirm password..."
                name="passwordConfInput"
                onChange={(e) => {
                  handelPasswordConfirm(e.target.value);
                }}
              />
              <p className="errorMassege" />
            </div>
            <button type="submit" className="submitBtn">
              update
            </button>
          </form>
        </div>
      </section>
    </div>
  );
};
